    public int[] addNegabinary(int[] arr1, int[] arr2) {
        int i = arr1.length - 1, j = arr2.length - 1;
        List<Integer> ans = new ArrayList<>();
        for (int c = 0; i >= 0 || j >= 0 || c != 0; --i, --j) {
            int a = i < 0 ? 0 : arr1[i];
            int b = j < 0 ? 0 : arr2[j];
            int x = a + b + c;
            c = 0;
            if (x >= 2) {
                x -= 2;
                c -= 1;
            } else if (x == -1) {
                x = 1;
                c += 1;
            }
            ans.add(x);
        }
        while (ans.size() > 1 && ans.get(ans.size() - 1) == 0) {
            ans.remove(ans.size() - 1);
        }
        Collections.reverse(ans);
        return ans.stream().mapToInt(x -> x).toArray();
    }

    public int[] toNegabinary(long num) {
        if (num == 0) {
            return new int[]{0};
        }
        List<Integer> list = new ArrayList<>();
        while (num != 0) {
            if (num % -2 == 0) {
                num = num >> 1;
                list.add(0);
            } else {
                list.add(1);
                if (num < 0) {
                    num -= 1;
                }
                num = num >> 1;
            }
            num *= -1;
        }
        int[] res = new int[list.size()];
        for (int i = 0, j = list.size() - 1; i < list.size(); i++, j--) {
            res[i] = list.get(j);
        }
        return res;
    }

    public long toValue(int[] arr) {
        long v = 0;
        for (int i = arr.length - 1; i >= 0; i--) {
            if (arr[i] == 0) continue;
            int index = arr.length - 1 - i;
            long pow = (long) Math.pow(2, index);
            if (index % 2 != 0) {
                pow *= -1;
            }
            v += pow;
        }
        return v;
    }
